DROP VIEW IF EXISTS `v_devices`;
CREATE VIEW `v_devices` AS select `d`.`iddevice` AS `iddevice`,`d`.`device_name` AS `device_name`,`d`.`state` AS `state`,`dt`.`device_type_name` AS `device_type_name`,`d`.`serialnumber` AS `serialnumber`,`d`.`note` AS `note`,`d`.`mac_address` AS `mac_address`,`d`.`device_photo` AS `photo` from (`device` `d` join `device_types` `dt` on((`d`.`device_types_iddevice_types` = `dt`.`iddevice_types`)));
