DROP VIEW IF EXISTS `v_logs`;
CREATE VIEW `v_logs` AS select `l`.`device_name` AS `device_name`,`l`.`status_code` AS `status_code`,`l`.`modify_user` AS `modify_user`,`l`.`modify_timestamp` AS `modify_timestamp`,`m`.`level` AS `level`,`t`.`text_translation` AS `text_translation`,`lc`.`idlocales` AS `idlocales`,`l`.`values` AS `values` from (((`logs` `l` join `messages` `m` on((`l`.`messages_idmessages` = `m`.`idmessages`))) join `translations` `t` on((`m`.`idmessages` = `t`.`messages_idmessages`))) join `locales` `lc` on((`t`.`locales_idlocales` = `lc`.`idlocales`)));