DROP VIEW IF EXISTS `v_users`;
CREATE VIEW `v_users` AS select `u`.`idusers` AS `idusers`,`ug`.`idusergroups` AS `idusergroups`,`u`.`username` AS `username`,`u`.`password` AS `password`,`u`.`email` AS `email`,`u`.`user_photo` AS `user_photo`,`u`.`modify_timestamp` AS `user_timestamp`,`u`.`modify_user` AS `user_modify`,`ug`.`group_name` AS `group_name`,`ug`.`description` AS `description`,`ug`.`modify_timestamp` AS `usergroup_timestamp`,`ug`.`modify_user` AS `usergroup_modify` from ((`users` `u` join `users_to_usergroups` `u2ug` on((`u`.`idusers` = `u2ug`.`users_idusers`))) join `usergroups` `ug` on((`u2ug`.`usergroups_idusergroups` = `ug`.`idusergroups`)));
